def fix_klasses_of_legacy_datasets(apps, schema_editor):
    """
    Replaces null (i.e. None) labels from klasses by the empty string
    for each legacy dataset where this problem may still occur.
    """

    try:
        Dataset = apps.get_model('dataset', 'Dataset')
        for dataset in Dataset.objects.only('klasses'):
            if dataset.klasses:
                dataset.klasses = map(lambda label: label or '', dataset.klasses)
                dataset.save()
    except LookupError:
        pass


